<?php namespace App\Http\Controllers; use App\Models\Room; use Illuminate\Http\Request; class Room extends Controller { public function _room() { $rooms = Room::with('type')->get(); return response()->json($rooms); } public function _store(Request $request) { $validated = $request->validate([ 'room_number' => 'required|unique:rooms', 'type_id' => 'required|exists:room_types,id', 'price' => 'required|numeric', 'status' => 'required|in:available,occupied,maintenance' ]); $room = Room::create($validated); return response()->json($room, 201); } public function _delete(Request $request) { $room = Room::findOrFail($request->room_id); $room->delete(); return response()->json(['message' => 'Room deleted successfully']); } }