<?php namespace App\Http\Controllers; use App\Models\Booking; use Illuminate\Http\Request; class RoombookEdit extends Controller { public function _update(Request $request) { $validated = $request->validate([ 'booking_id' => 'required|exists:bookings,id', 'check_in' => 'sometimes|date', 'check_out' => 'sometimes|date|after:check_in', 'room_id' => 'sometimes|exists:rooms,id', 'status' => 'sometimes|in:pending,confirmed,cancelled' ]); $booking = Booking::findOrFail($request->booking_id); $booking->update($validated); return response()->json($booking); } }