<?php namespace App\Http\Controllers; use App\Models\Staff; use Illuminate\Http\Request; class Staff extends Controller { public function _staff() { $staff = Staff::all(); return response()->json($staff); } public function _store(Request $request) { $validated = $request->validate([ 'name' => 'required|string', 'position' => 'required|string', 'email' => 'required|email|unique:staff', 'phone' => 'required|string' ]); $staff = Staff::create($validated); return response()->json($staff, 201); } public function _delete(Request $request) { $staff = Staff::findOrFail($request->staff_id); $staff->delete(); return response()->json(['message' => 'Staff member deleted successfully']); } } 